        Int br=n-2*a-1;
        if(bl>br) continue;

        // [bl, br]
        Int l=bl,r=br;
        while(l+1<r){
          Int m=(l+r)>>1;
          if(calc(a,m)>calc(a,m+1)) l=m;
          else r=m;
        }
