		}

        double low=0.0, top=PI, ans=top, mid;
        while(top - low > eps){
			mid = (low + top) * 0.5;
            if(ok(mid)){
				ans = min(ans, mid);
				top = mid - eps;
            } else {
				low = mid + eps;
            }
        }
        printf("%.12f\n", ans);
	}
	return 0;
}

