R calc(int a, int b) {
    //P l = polar<R>(1, 2*PI / n * a);
    //P r = polar<R>(1, 2*PI / n * b);
    //P base = P(1, 0);
    //return cross(l-base, r-base)/2;
    return cross(po[a], po[b])/2;
}

ll solve(R md) {
    ll ans = 0;
    int q = 1;
    for (int p = 1; p < n; p++) {
        int U = (p+n)/2;
        int l = p, r = U+1;
        while (r-l > 1) {
            int mi = (l+r)/2;
            if (calc(p, mi) < md) {
                l = mi;
            } else {
                r = mi;
            }
        }
        q = r;
        ans += 2*max(0, q-p-1);
        if (q == U+1 && (p+n) % 2 == 0) ans--;
    }
    ans *= n;
    ans /= 3;
    return ans;
}

