const double pi = 3.1415926535;

int main(){
    long N, K;
    cin >> N >> K;
    double left = 0, right = 100;
    for(int i = 0; i < 100; i++){
        double middle = (left + right) / 2;
        long less_middle = 0;
        for(long j = 1; j < N; j++){

            long left2 = j - 1, right2 = (N - j) / 2 + 1; // [left2, right2)
            if(left2 > right2){
                continue;
            }
            while(left2 + 1 < right2){
                long middle2 = (left2 + right2) / 2;
                long l = N - j - middle2;
                double area = 2 * sin(pi / N * j) * sin(pi / N * middle2) * sin(pi / N * l);
                if(area >= middle){
                    right2 = middle2;
                }else{
                    left2 = middle2;
                }
            }

            long max_k = left2 + 1;
            if(j == max_k){
                continue;
            }
            less_middle += (max_k - j) * N * 2;
            if(N % 3 == 0 && j == N / 3 && max_k > j){
                less_middle += N / 3 - N * 2;
            }else{
                if(max_k > j){
                    less_middle += N - N * 2;
                }
                if((N - j) % 2 == 0 && max_k > (N - j) / 2){
                    less_middle += N - N * 2;
                }
            }
        }
        if(less_middle < K){
            left = middle;
        }else{
            right = middle;
        }
    }
    fcout << left << endl;
}

