signed main() {
    std::ios::sync_with_stdio(false);
    std::cin.tie(0);

    int N, K;
    cin >> N >> K;
    assert(N <= 1000);

    vector<P> p;
    double theta = 0;
    double t = 2. * PI / N;
    rep(i, N) {
        p.emplace_back(cos(theta), sin(theta));
        theta += t;
    }

    vector<pair<double, int>> areas;
    rep2(i, 1, N) {
        rep2(j, i + 1, N) {
            int a = i, b = j - i, c = N - j;
            if (a == b && b == c && c == a) {
                // 正三角形
                areas.emplace_back(areaTriangle(p[0], p[i], p[j]), N / 3);
            } else if (a == c && a != b && b != c) {
                // 二等辺三角形(正三角形を除く)
                areas.emplace_back(areaTriangle(p[0], p[i], p[j]), N);
            // } else if (a != b && b != c && c != a) {
            } else if ((a < b && b < c) || (a < c && c < b)) {
                // 三辺の長さが異なる三角形
                areas.emplace_back(areaTriangle(p[0], p[i], p[j]), N);
            }
        }
    }
    sort(all(areas));

    rep(i, areas.size()) {
        if (K <= areas[i].second) {
            cout << fixed << setprecision(10) << areas[i].first << endl;
            return 0;
        }
        K -= areas[i].second;
    }

}

