}

double calcheight(int a,int b,int c,double Arg){
  P base=P(0,1);
  P pa= base * P( cos(-Arg*a) , sin(-Arg*a) );
  P pb= base * P( cos(-Arg*b) , sin(-Arg*b) );
  P pc= base * P( cos(-Arg*c) , sin(-Arg*c) );
  return getDist(pa,pb,pc);
}

double calcarea(int a,int b,int c,double Arg){
  P base=P(0,1);
  P pa= base * P( cos(-Arg*a) , sin(-Arg*a) );
  P pb= base * P( cos(-Arg*b) , sin(-Arg*b) );
  P pc= base * P( cos(-Arg*c) , sin(-Arg*c) );
  return abs( cross(pa-pb,pc-pb) ) * 0.5;
}

