void odj(pdd &a, pdd b)
{
	a.first-=b.first;
	a.second-=b.second;
}

double pole(pdd s, pdd a, pdd b)
{
	odj(a, s);
	odj(b, s);
	return abs(a.first*b.second-a.second*b.first)/2;
}

ll licz(double v)
{
	//~ int ile=0;
	//~ for (int i=0; i<n; i++)
		//~ for (int j=0; j<i; j++)
			//~ for (int l=0; l<j; l++)
				//~ if (pole(pun[i], pun[j], pun[l])<=v)
					//~ ile++;
	//~ return ile;
	ll ret=0;

	int w=0;
	for (int i=2; i<n; i++)
	{
		if (pole(pun[0], pun[i], pun[i/2])<=v)
		{
			ret+=i-1;
			continue;
		}
		if (pole(pun[0], pun[i], pun[1])>v)
		{
			continue;
		}
		while(pole(pun[0], pun[i], pun[w+1])<=v)
			w++;
		while(pole(pun[0], pun[i], pun[w])>v)
			w--;
		ret+=2*w;
	}

	ret*=n;
	ret/=3;
	return ret;
}

