    cin >> N >> K;
    double left = 0, right = 100;
    for(int i = 0; i < 100; i++){
        double middle = (left + right) / 2;
        long less_middle = 0;
        for(long j = 1; j < N; j++){
            long max_k = 0;
            for(max_k = j; j + max_k * 2 <= N; max_k++){
                long l = N - j - max_k;
                double area = 2 * sin(pi / N * j) * sin(pi / N * max_k) * sin(pi / N * l);
                if(area >= middle){
                    break;
                }
            }
            if(j == max_k){
                continue;
            }
            less_middle += (max_k - j) * N * 2;
            if(N % 3 == 0 && j == N / 3 && max_k > j){
                less_middle += N / 3 - N * 2;
            }else{
                if(max_k > j){
                    less_middle += N - N * 2;
                }
                if((N - j) % 2 == 0 && max_k > (N - j) / 2){
                    less_middle += N - N * 2;
                }
            }
        }
        if(less_middle < K){
            left = middle;
        }else{
            right = middle;
        }
    }
    fcout << left << endl;
}

