  cin>>n>>k;
  if((ll)n*(n-1)*(n-2)/6 == k){
    puts("1.29903810568");
    return 0;
  }
  Real u = 2.0*M_PI/(Real)n;
  P po = exp(P(0,u));
  ppp[0] = P(1,0);
  REP(i,n)ppp[i+1] = ppp[i]*po;
  Real low = 0.0, high = 1.3;
  // DEBUG(calc(n,2.0));
  REP(_,40){
    Real mid = (low+high)/2.0;
    ll cnt = calc(n,mid);
    // DEBUG(mid);
    // DEBUG(cnt);
    if(cnt > k){
      high = mid;
    }else{
      low = mid;
    }
  }
  printf("%.12lf\n",low);
  return 0;
}

