		return 0;
	}
	n%3 ? 0 : k-=n/3; //È¥³ýµÈ±ßÈý½ÇÐÎ 
	k = (k+n-1)/n;
	Ldb l = 0 , r = (Ldb)3 * sqrt(3) / (Ldb)4.0;
	while (r-l > eps) {
		Ldb mid = (l + r) / (Ldb)2.00;
		if (go(mid)) l = mid; else r = mid;
	}
	printf("%.15lf\n",(double)l);
	return 0;
}

