constexpr ll TEN(int n) { return (n==0) ? 1 : 10*TEN(n-1); }

int n;
using R = double;
using P = complex<R>;
constexpr R PI = acos(R(-1));

R cross(P a, P b) { return a.real()*b.imag() - a.imag()*b.real(); }
R dot(P a, P b) { return a.real()*b.real() + a.imag()*b.imag(); }

R calc(int a, int b) {
    P l = polar<R>(1, 2*PI / n * a);
    P r = polar<R>(1, 2*PI / n * b);
    P base = P(1, 0);
    return cross(l-base, r-base)/2;
}

ll solve(R md) {
    ll ans = 0;
    int q = 1;
    for (int p = 1; p < n; p++) {
        int U = (p+n)/2;
        int l = p, r = U+1;
        while (r-l > 1) {
            int mi = (l+r)/2;
            if (calc(p, mi) < md) {
                l = mi;
            } else {
                r = mi;
            }
        }
        q = r;
        ans += 2*max(0, q-p-1);
        if (q == U+1 && (p+n) % 2 == 0) ans--;
    }
    ans *= n;
    ans /= 3;
    return ans;
}

int main() {
    ios::sync_with_stdio(0);
    cout.precision(20);
    ll k;
    cin >> n >> k;
    /*for (int i = 1; i < n; i++) {
        for (int j = i; j < n; j++) {
            cout << calc(i, j) << " ";
        } cout << endl;
    }*/
    R l = -0.1, r = 4;
    for (int i = 0; i < 100; i++) {
        R md = (l+r)/2;
        if (solve(md) < k) {
            l = md;
        } else {
            r = md;
        }
    }
    cout << l << endl;
    return 0;
}

