{
	//X=1.0;
	ll sum=0;
	for(int i=1;i<n;i++) sum+=count(i,X);
	//for(int i=0;i<n;i++) printf("%d ",count(i,X));
	//puts("");
	//return 0;
	return sum*(ll) n>=K;
}
