double S(int x, int y){
  return (sins[x] + sins[y] + sins[n-x-y])/2.0;
}

// s以下の面積が何通りとれるか
long cnt(double s){
  long ret = 0;
  // x < y < z が何通りとれるか
  for(int x=1; x<=n/3; x++){
    if(3*x+3>n) break;
    if(S(x, (n-x-1)/2) < s){
      ret += (n-x-1)/2;
      continue;
    }
    if(S(x, x+1) >s){
      continue;
    }
    int ly=x+1, ry=(n-x-1)/2; // lyは必ずs以下と保証される
    while(ry-ly>1){
      int my = (ry+ly)/2;
      if(S(x, my) > s) ry=my;
      else ly=my;
    }
    ret += ly;
  }
  ret = ret*n; // 始点n通り＊反対周り

  // 正三角形
  if(n%3==0 && s>=S(n/3,n/3)) ret+=n/3;

  // 二等辺三角形
  long ll = upper_bound(all(iso), s) - iso.begin();
  ret += ll*n;

  return ret;
}

