  }
  Real low = 0.0, high = 4.0;
  // DEBUG(calc(n,2.0));
  REP(_,100){
    Real mid = (low+high)/2.0;
    ll cnt = calc(n,mid);
    // DEBUG(mid);
    // DEBUG(cnt);
    if(cnt > k){
      high = mid;
    }else{
      low = mid;
    }
  }
  printf("%.12lf\n",low);
  return 0;
}

