int N,K;
P p[MX];


double area(int a, int b, int c) {
    return abs(cross(p[0],p[a],p[a+b]))/2;
}

ll get(ld mid) {
    ll ans = 0;
    FOR(i,1,N-1) {
        int mn = 0, mx = (N-i)/2;
        while (mn < mx) {
            int m = (mn+mx+1)/2;
            if (area(i,m,N-i-m) <= mid) mn = m;
            else mx = m-1;
        }
        ans += 2*mx;
        if (2*mx == N-i) ans --;
    }
    return ans;
}

