ll N,K;

void solve() {
	int i,j,k,l,r,x,y; string s;
	double PI=atan(1)*4;

	cin>>N>>K;
	vector<double> D;

	if(N>1000) return;
	for(x=1;x<N;x++) for(y=x+1;y<N;y++) {
		double dx = 2*PI*x/N;
		double dy = 2*PI*y/N;
