using ld = long double;

ll n, k;

const ld PI = acos(-1);

inline ld len(int d)
{
	return sqrt(2 - 2 * cos(PI * 2 / n * d));
}

inline ld cosform(int a_, int b_, int c_)
{
	ld a = len(a_);
	ld b = len(b_);
	ld c = len(c_);
	ld up = a * a + b * b - c * c;
	ld dn = a * b * 2;
	return (ld)up / dn;
}

bool cond(ld s)
{
