ld len_m[234567];
inline ld len(int d)
{
	if (len_m[d] >= 0)return len_m[d];
	return len_m[d] = sqrt(2 - 2 * cos(PI * 2 / n * d));
}

inline ld cosform(int a_, int b_, int c_)
{
	ld a = len(a_);
	ld b = len(b_);
	ld c = len(c_);
	ld up = a * a + b * b - c * c;
	ld dn = a * b * 2;
	return (ld)up / dn;
}

bool cond(ld s)
{
	ll cnt = 0;
	for (int f = 1; f < n - 1; ++f)
	{
		int hi = (n - f) / 2. + 1;
		int lo = 0;
		while (lo + 1 < hi)
		{
			int md = (hi + lo) / 2;
			int oth = n - md - f;
			ld costh = cosform(md, f, oth);
			ld sinth = sqrt(1. - costh * costh);
			ld area = len(md) * len(f) * sinth / 2.;
			if (area <= s)
			{
				lo = md;
			}
			else
			{
				hi = md;
			}
		}
		ll plus = lo + lo;
		if (plus > n - f - 1)plus = n - f - 1;
		cnt += plus;
	}
	return (cnt > k);
}

