double heron(double a, double b, double c){
    double s = (a + b + c) / 2;
    return sqrt(s * (s-a) * (s-b) * (s-c));
}

int N;
ll K;
double px[200001], py[200001];

double f(int i, int j) {
    return hypot(px[i] - px[j], py[i] - py[j]);
}
double g(int i, int j, int k) {
    double a = f(i, j), b = f(j, k), c = f(k, i);
    return heron(a, b, c);
}

int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    while(cin >> N >> K) {
        rep(i, N) {
            double w = 2 * M_PI * i / N;
            px[i] = cos(w);
            py[i] = sin(w);
        }

        double l = 0, r = 2, m;
        rep(i_, 60) {
            m = (l + r) / 2;
            ll z = 0;
            for(int i = 1; i < N - 1; ++i) {
                int j = (N + i) / 2;
                int L = i + 1, R = j + 1, M;
                if(g(0, i, i+1) > m) continue;
                while(R - L > 1) {
                    M = (L + R) / 2;
                    (g(0, i, M) <= m ? L : R) = M;
                }
                if(L == j)z += (ll)(N - i - 1)*i;
                else z += 2 * (L - i)*i;
            }
            (z >= K ? r : l) = m;
        }
        cout << setprecision(20) << l << endl;
    }
}

