#define gc() getchar()
using namespace std;
typedef long long LL;
typedef long double Ldb;

int n; LL k;
const Ldb eps = 1e-15;
const Ldb pi = 3.1415926535897932384626433832795;//0238 4197 1693 9937 5105

inline LL rd() {
	LL x=0,f=1; char ch=gc();
	while (ch>'9'||ch<'0') ch=='-'?f*=-1:0,ch=gc();
	while (ch>='0'&&ch<='9') x=x*10+ch-'0',ch=gc();
	return x*f;
}

inline Ldb S(int p1,int p2) {
	int p3 = n - p1 - p2;
	Ldb a,b,c,p,s;
	a = (Ldb)2 * sin(pi*p1/(Ldb)n);
	b = (Ldb)2 * sin(pi*p2/(Ldb)n);
	c = (Ldb)2 * sin(pi*p3/(Ldb)n);
	p = ( a + b + c ) / (Ldb)2;
	s = sqrt(p * (p-a) * (p-b) * (p-c));
	return s;
} 

void spj(LL &ret,int a,Ldb x) {
	if (n%3 == 0 && a == n/3) return ;
	if ( 2*a+1 <= n && S(a,a) >= x ) ret-=2;
	if (S( (n-a)>>1 , (n-a)>>1 ) >= x) ret++;
}


bool go(Ldb x) {
	LL ret = 0LL;
	for (int i=1;i<=n-2;i++) {
		int t = (n-i-1) >> 1;
		int l = 1 , r = t;
		while (l < r) {
			int mid = (l + r) >> 1;
			if (S(i,mid) >= x) r = mid; else l = mid + 1;
		}
		if (l == t)
			ret += 2LL * (S(i,t) >= x);
		else
			ret += 2LL * (t-l+1);
		if ((n-i)%2 == 0) spj(ret,i,x);
	}
	return ret >= k;
}

int main() {
	n = rd() , k = 1LL*n*(n-1)*(n-2)/6LL - rd();
	if (n%3==0 && k<=n/3) { 
		printf("%.15lf\n",3*sqrt(3)/4.0);
		return 0;
	}
	n%3 ? 0 : k-=n/3; //È¥³ýµÈ±ßÈý½ÇÐÎ 
	k = (k+n-1)/n;
	Ldb l = 0 , r = 3*sqrt(3) / 4.0;
	while (r-l > eps) {
		Ldb mid = (l + r) / 2.00;
		if (go(mid)) l = mid; else r = mid;
	}
	printf("%.15lf\n",(double)l);
	return 0;
}

