inline D cross(const P &p,const P &q){
    return real(p)*imag(q)-imag(p)*real(q);
}

inline D getS(int i,int j,int k){
    return abs(cross(p[j]-p[i],p[k]-p[i]))/2;
}

bool C(D s){
    int cnt=0;
    for(int i=1;i<N-1;i++){
        int lb=-1,ub=(i+1)/2;
        while(ub-lb>1){
            int mid=(ub+lb)/2;
            if(getS(N-1,i,mid)>s)ub=mid;
            else lb=mid;
        }
        cnt+=ub*2;
        if(i%2&&ub==(i+1)/2)cnt--;
    }

    if(N%3==0&&getS(0,N/3,N/3*2)<=s)cnt+=2;
    cnt/=3;
    cnt*=N;
    return cnt>=K;
}

