    double low = 0;
    double high = 10;
    while (high - low > 1e-12) {
        double mid = 0.5*(low + high);
        if (nbelow(mid, int(N)) >= K)
            high = mid;
        else
            low = mid;
    }

    cout << high << '\n';


    return 0;
}

