signed main(){
  Int n,k;
  cin>>n>>k;
  k--;
  using D = double;
  const D PI = acos(-1.0);
  vector<D> ss(n+1);
  for(Int i=0;i<=n;i++)
    ss[i]=sin((D)i/n*PI);

  auto check=
    [&](D x)->Int{
      auto calc=[&](Int a,Int b)->D{return ss[a+b]*ss[a]*ss[b];};
      Int cnt=0;
      // seisankaku (a = b = c)
      if(n%3==0){
        Int e=n/3;
        if(calc(e,e)<=x) cnt+=e;
      }
      // nitouhen (a = b)
      for(Int a=1;a*2<n;a++){
        Int b=a;
        if(calc(a,b)<=x) cnt+=n; 
      }
      // sonota (a < b, a < c)
      for(Int a=1;a*3<n;a++){
        // a < b
        Int bl=a+1;
        // a < c = n - (a + b)
        Int br=n-2*a-1;

        // [bl, br]
        Int l=bl,r=br;
        while(l+1<r){
          Int m=(l+r)>>1;
          if(calc(a,m)>calc(a,m+1)) l=m+1;
          else r=m;
        }
        Int t=calc(a,l)<calc(a,r)?l:r;
        if(calc(a,t)>x) continue;
        {
          bl--;
          Int bt=t;
          while(bl+1<bt){
            Int bm=(bl+bt)>>1;
            if(calc(a,bm)<=x) bt=bm;            
            else bl=bt;
          }
          bl=bt;
        }
        {
          br++;
          Int bt=t;
          while(bt+1<br){
            Int bm=(bt+br)>>1;
            if(calc(a,bm)<=x) bt=bm;            
            else br=bt;
          }
          br=bt;
        }        
        cnt+=(br-bl+1)*n;        
        // b == c
        if((n-a)%2==0){
          Int b=(n-a)/2;
          if(bl<=b&&b<=br) cnt-=n;
        }
      }
      return cnt>=k;
    };

  D L=0,R=1;
  for(Int k=0;k<50;k++){
    D M=(L+R)/2;
    if(check(M)) R=M;
    else L=M;
  }
  cout<<R*2<<endl;
  return 0;
}

