const LL  INFLL  = 0x7f7f7f7f7f7f7f7fLL;
const int INFint = 0x7f7f7f7f;  //Works with memset(..).
double M_PI = acos(-1);



LL nbelow(double area, int N) {
    //Longest chord spans i steps along circumference.
    LL rr = 0;
    iii (1, N / 2 + 1) {
        int start = i;
        int finish = (N - i) / 2;
        if (start > finish)
            continue;
        double base = 2*sin((M_PI*i)/N);
        double height = 2*area/base;
        double x = height - cos((M_PI*i)/N);
        if (x < 1) {
            double angle = acos(x);
            double arc = M_PI - (M_PI*i)/N - angle;
            finish = min(finish, int(arc * N / (2*M_PI)));
        }
        if (finish < start)
            continue;
        //Don't count finish, count start only once.
        if (finish > start) {
            LL total = 2*(finish - start - 1) + 1;
            rr += total * LL(N);
        }
        if (finish == (N - i - finish)) {
            if (finish == i) {
                rr += N / 3;
            } else {
                rr += N;
            }
        } else {
            if (finish == start)
                rr += N;
            else
                rr += 2*N;
        } 
    }

    //printf("For %g we have %d\n", area, int(rr));
    return rr;
}



