ll n, k;

const ld PI = acos(-1);

ld len_m[234567];
inline ld len(int d)
{
	if (len_m[d] >= 0)return len_m[d];
	return len_m[d] = sqrt(2 - 2 * cos(PI * 2 / n * d));
}

inline ld cosform(int a_, int b_, int c_)
{
	ld a = len(a_);
	ld b = len(b_);
	ld c = len(c_);
	ld up = a * a + b * b - c * c;
	ld dn = a * b * 2;
	return (ld)up / dn;
}

bool cond(ld s)
{
	ll cnt = 0;
	for (int f = 1; f < n - 1; ++f)
	{
		int hi = (n - f) / 2. + 1;
		int lo = 0;
		while (lo + 1 < hi)
		{
			int md = (hi + lo) / 2;
			int oth = n - md - f;
			ld costh = cosform(md, f, oth);
			ld sinth = sqrt(1. - costh * costh);
			ld area = len(md) * len(f) * sinth / 2.;
			if (area <= s)
			{
				lo = md;
			}
			else
			{
				hi = md;
			}
		}
		ll plus = lo + lo;
		if (plus > n - f - 1)plus = n - f - 1;
		cnt += plus;
	}
	return (cnt >= k);
}

const ld EPS = 1e-15;

int main(void)
{
	cin >> n >> k;
	k *= 3;
	k /= (n);
	fill(len_m, len_m + 234567, -1.);
	ld hi = PI, lo = 0;
	while(hi - lo >= EPS)
	{
		ld md = (hi + lo) / 2;
		if (cond(md))
		{
			hi = md;
		}
		else
		{
			lo = md;
		}
	}
	cout.precision(20);
	cout << hi << endl;
	return 0;
}

