int n;
double sins[200005];
vector<double> iso;

double S(int x, int y){
  return (sins[x] + sins[y] + sins[n-x-y])/2.0;
}

// s以下の面積が何通りとれるか
long cnt(double s){
  long ret = 0;
  // x < y < z が何通りとれるか
  for(int x=1; x<=n/3; x++){
    if(3*x+3>n) break;
    if(S(x, (n-x-1)/2) < s){
      ret += (n-x-1)/2 - x;
      continue;
    }
    if(S(x, x+1) >s){
      continue;
    }
    int ly=x+1, ry=(n-x-1)/2; // lyは必ずs以下と保証される
    while(ry-ly>1){
      int my = (ry+ly)/2;
      if(S(x, my) > s) ry=my;
      else ly=my;
    }
    ret += ly - x;
  }
  ret = ret*n*2; // 始点n通り＊反対周り

  // 正三角形
  if(n%3==0 && s>=S(n/3,n/3)) ret+=n/3;

  // 二等辺三角形
  long ll = upper_bound(all(iso), s) - iso.begin();
  ret += ll*n;

  return ret;
}

int main(){
  long k;
  cin>>n>>k;

  const double p = atan(1)*4.0;
  rep(i,n+1) sins[i] = sin(2.0*p/n*i);

  // 2等辺三角形の面積列挙
  repl(i,1,n-1) if((n-i)%2==0){
    iso.pb(S(i,(n-i)/2));
  }
  sort(all(iso));

  double sl=0, sr=2;
  rep(_,50){
    double sm = (sl+sr)/2.0;
    if(cnt(sm) < k) sl = sm;
    else sr = sm;
  }

  printf("%.11f\n", sr);

  return 0;
}

