  P x = ppp[a];
  ll bai = n;
  if(n%3==0)bai/=3;
  FOR(b,1,n-1){
    P y = ppp[b];
    int low = b+1, high = n-1;
    int cnt = (high-low+1);
    // seisannkakkei guard
    if(n%3==0 && b==n/3){
      cnt--;
    }
    if(cnt%2==1){
      int cent = low + cnt/2;
      P z = ppp[cent];
      if(sur(x,y,z) < s){
        ans += cnt;
        continue;
      }
    }
    cnt /= 2;
    high = low + cnt - 1 + 1;
    low--;
    while(low+1<high){
      int mid = (low+high)/2;
      P z = ppp[mid];
      if(sur(x,y,z) < s){
        low = mid;
      }else{
        high = mid;
      }
    }
    ans += 2ll*(low-b);
  }
  // DEBUG(ans);
  ans /= 3;
  ans *= n;
  return ans;
}

