  REP(i,n)ppp[i+1] = ppp[i]*po;
  Real low = 0.0, high = 1.3;
  // DEBUG(calc(n,2.0));
  REP(_,40){
    Real mid = (low+high)/2.0;
    ll cnt = calc(n,mid);
    // DEBUG(mid);
    // DEBUG(cnt);
    if(cnt >= k){
      high = mid;
    }else{
      low = mid;
    }
  }
  printf("%.12lf\n",low);
  return 0;
}

