int64 calc(double t,int a,double *ss,int n){
  if(3*a==n){
    return 2*ss[a]*ss[a]*ss[a]<=t?n/3:0;
  }
  int64 res=0;
  if(2*ss[a]*ss[a]*ss[n-2*a]>t) return 0;
  res+=n;//a,a,n-2a
  int max=(n-a)/2;
  if(2*ss[a]*ss[max]*ss[n-a-max]<=t){
    res+=2*n*(max-a);
    if(max==n-a-max) res-=n;
    return res;
  }
  int l=a;
  int r=max;
  while(l+1<r){
    int m=(l+r)/2;
    if(2*ss[a]*ss[m]*ss[n-a-m]<=t){
      l=m;
    } else {
      r=m;
    }
  }
  res+=2*n*(l-a);
  return res;
}

