class MQ {
	private:
	deque<ll> q;
	public:
	void push(ll val) {
		while(!(q.empty()) && q.back() > val) {
			q.pop_back();
		}
		q.push_back(val);
	}
	void pop(ll val) {
		if(!q.empty() && q.front() == val) {
			q.pop_front();
		}
	}
	ll front() {
		return q.front();
	}
};
