		while(2 * (a[i] - a[h]) >= t)
			h++;
		dp[i] = min(t + get(2, 1, 0, n + 1, h - 1, i), get(1, 1, 0, n + 1, 0, h - 1));
		upd(1, 1, 0, n + 1, i, i + 1, dp[i] - 2 * (a[i + 1] - a[i]));
		upd(2, 1, 0, n + 1, i, i + 1, dp[i]);
	}
	cout << e - a[n] + dp[n] << endl;
