    X[N+1] = E;
    dp[0] = X[1];
    seg1.add(0,1,dp[0]);
    seg2.add(0,1,dp[0]);
    for(int i=1;i<=N;i++){
        int id = lower_bound(X.begin(),X.end(),X[i]-T/2)-X.begin();
        //cerr << i << " " << id << " " << seg2.getmin(id-1,i) << " " << seg1.getmin(0,id-1) << endl;
        dp[i] = seg2.getmin(id-1,i)+T+X[i+1]-X[i];
        if(id>1) dp[i] = min(dp[i],seg1.getmin(0,id-1)+X[i+1]-X[i]);
        seg1.add(i,i+1,dp[i]);
        seg1.add(0,i,3*(X[i+1]-X[i]));
