void Update(LL now,LL l,LL r,LL x,LL v)
{
	if (l==r) {Segt[now]=v; return;}
	LL mid=(l+r)>>1;
	if (x<=mid) Update(now<<1,l,mid,x,v);
	else Update(now<<1|1,mid+1,r,x,v);
	Segt[now]=min(Segt[now<<1],Segt[now<<1|1]);
}

LL Query(LL now,LL l,LL r,LL l1,LL r1)
{
	if (l>r1 || r<l1) return 1e17;
	if (l1<=l && r<=r1) return Segt[now];
	LL mid=(l+r)>>1;
	return min(Query(now<<1,l,mid,l1,r1),Query(now<<1|1,mid+1,r,l1,r1));
}

