		for (int j = max(0, i - 100000000 / n); j < i; j++) {
			f[i] = min(f[i], calc(j, i));		
		}
		for (int j = 0; j < min(i, 100000000 / n); j++) {
			f[i] = min(f[i], calc(j, i));	
		}
		while (p + 1 < i && calc(p, i) > calc(p + 1, i)) ++p;
		f[i] = min(f[i], calc(p, i));
	}
