#define ll long long
using namespace std;

const int N=100005;
const int M=2005;
const ll inf=1000000000000000000ll;

struct ST{
	int l,r;
	ll mn1,mn2,tag;
}t[N<<2];
int n;
ll e,T,mn;
int f[N];
ll dp[M][M],x[N];

void update(int k){
	t[k].mn1=min(t[k<<1].mn1,t[k<<1|1].mn1);
	t[k].mn2=min(t[k<<1].mn2,t[k<<1|1].mn2);
}

void build(int k,int l,int r){
	t[k].l=l; t[k].r=r;
	t[k].mn1=t[k].mn2=inf; t[k].tag=0;
	if (l==r) return;
	int mid=(l+r)>>1;
	build(k<<1,l,mid);
	build(k<<1|1,mid+1,r);
}

void add(int k,ll w){
	t[k].tag+=w;
	t[k].mn1+=w;
	t[k].mn2+=w;
}

void pushdown(int k){
	if (t[k].tag){
		add(k<<1,t[k].tag);
		add(k<<1|1,t[k].tag);
		t[k].tag=0;
	}
}

void modifys(int k,int L,int R,ll w){
	if (L<=t[k].l&&t[k].r<=R){
		add(k,w);
		return;
	}
	pushdown(k);
	int mid=(t[k].l+t[k].r)>>1;
	if (L<=mid) modifys(k<<1,L,R,w);
	if (R>mid) modifys(k<<1|1,L,R,w);
	update(k);
}

void modifyp(int k,int p,ll w){
	if (t[k].l==t[k].r){
		t[k].mn1=min(t[k].mn1,w);
		t[k].mn2=min(t[k].mn2,w-2ll*x[p]);
		return;
	}
	pushdown(k);
	int mid=(t[k].l+t[k].r)>>1;
	if (p<=mid) modifyp(k<<1,p,w);
	else modifyp(k<<1|1,p,w);
	update(k);
}

ll query1(int k,int L,int R){
	if (L<=t[k].l&&t[k].r<=R) return t[k].mn1;
	pushdown(k);
	int mid=(t[k].l+t[k].r)>>1;
	if (R<=mid) return query1(k<<1,L,R);
	else if (L>mid) return query1(k<<1|1,L,R);
	else return min(query1(k<<1,L,R),query1(k<<1|1,L,R));
}

ll query2(int k,int L,int R){
	if (L<=t[k].l&&t[k].r<=R) return t[k].mn2;
	pushdown(k);
	int mid=(t[k].l+t[k].r)>>1;
	if (R<=mid) return query2(k<<1,L,R);
	else if (L>mid) return query2(k<<1|1,L,R);
	else return min(query2(k<<1,L,R),query2(k<<1|1,L,R));
}

void init(){
	int i,l,r,mid;
	for (i=1;i<=n;i++){
		l=0; r=i-1;
		while (l<r){
			mid=(l+r+1)>>1;
			if (2*(x[i]-x[mid])>=T) l=mid;
			else r=mid-1;
		}
		f[i]=l;
	}
}

void solve1(){
	int i,j;
	for (i=0;i<=n+1;i++)
		for (j=0;j<=n+1;j++) dp[i][j]=inf;
	dp[1][1]=x[1];
	for (i=1;i<=n;i++)
		for (j=1;j<=i;j++) if (dp[i][j]<inf){
			dp[i+1][i+1]=min(dp[i+1][i+1],dp[i][j]+max(2*(x[i]-x[j]),T)+x[i+1]-x[i]);
			dp[i+1][j]=min(dp[i+1][j],dp[i][j]+x[i+1]-x[i]);
		}
	printf("%lld\n",dp[n+1][n+1]);
}

int main(){
	int i,j;
	scanf("%d%lld%lld",&n,&e,&T);
	for (i=1;i<=n;i++) scanf("%lld",&x[i]);
	init();
	x[n+1]=e;
	//if (n<=2000){solve1();return 0;}
	build(1,1,n+1);
	modifyp(1,1,x[1]);
	for (i=1;i<=n;i++){
		mn=query1(1,f[i]+1,i);
		modifyp(1,i+1,mn+T+x[i+1]-x[i]);
		if (f[i]>0){
			mn=query2(1,1,f[i]);
			modifyp(1,i+1,mn+2ll*x[i]+x[i+1]-x[i]);
		}
		modifys(1,1,i,x[i+1]-x[i]);
	}
	printf("%lld\n",query1(1,n+1,n+1));
	return 0;
}

