bool stack_better(int j1, int j2) {
    return dp[j1] - x[j1] - 2LL * x[j1 + 1] < dp[j2] - x[j2] - 2LL * x[j2 + 1];
}

bool deque_better(int i, int j) {
    return dp[i] - x[i] < dp[j] - x[j];
}

int main() {
    //ifstream cin("f.in");
    int n, j = -1, bst = -1; long long t, e;
    cin >> n >> e >> t;
    for (int i = 1; i <= n; ++ i) {
        cin >> x[i];
    }
    dp[0] = 0;
    dq.push_back(0);
    for (int i = 1; i <= n; ++ i) {
        dp[i] = 1LL << 60;

        /// the ones whose time surpassed t
        while (j + 1 < i && 2LL * (x[i] - x[j + 2]) >= t) {
            j += 1;
            if (bst == -1 || stack_better(j, bst)) {
                bst = j;
            }
        }
        if (j >= 0) {
            dp[i] = min(dp[i], dp[bst] - (x[bst] + 2LL * x[bst + 1]) + 3LL * x[i]);
        }

        /// time did not surpass t
        while (!dq.empty() && 2LL * (x[i] - x[dq.front() + 1]) >= t) {
            dq.pop_front();
        }
        if (!dq.empty()) {
            dp[i] = min(dp[i], dp[dq.front()] - x[dq.front()] + x[i] + t);
        }
        while (!dq.empty() && dp[i] - x[i] < dp[dq.back()] - x[dq.back()]) {
            dq.pop_back();
        }
        dq.push_back(i);
    }
    cout << dp[n] + (e - x[n]) << "\n";
    return 0;
}

