	dp1.update(0, 0);
	dp2.update(0, -2*x[0]);
	rep(i,n){//dp[i+1]
		int lb = -1, ub = i;
		while (ub - lb > 1) {
			int mi = (ub + lb) / 2;
			if (2 * (x[i] - x[mi]) > t)lb = mi;
			else ub = mi;
		}

		//0~lb ub~i
		int X1, X2;
		X1 = dp1.get(ub, i+1) + t;
		X2 = dp2.get(0, ub) + 2 * x[i];
//		cout << ub << ' ' << X1 << ' ' << X2 << endl;
		dp1.update(i+1, min(X1, X2));
