void upd(int i,int lef,int rig,int x,long long int val){
	if(lef == x && rig == x){
		a[i] = val;
		return;
	}
	int m = (lef+rig)/2;
	if(x <= m)
		upd(i*2,lef,m,x,val);
	else
		upd(i*2+1,m+1,rig,x,val);
	a[i] = min(a[i*2],a[i*2+1]);
}
long long int query(int i,int lef,int rig,int from,int to){
	if(lef == from && rig == to)
		return a[i];
	int m = (lef+rig)/2;
	if(to <= m)
		return query(i*2,lef,m,from,to);
	if(from > m)
		return query(i*2+1,m+1,rig,from,to);
	return min(query(i*2,lef,m,from,m),query(i*2+1,m+1,rig,m+1,to));
}
