#define N 100001
#define INF 1e15+7
struct SegmentTree {
private:
    int nn; vector<ll> node;
public:
    SegmentTree(int sz) {
        nn = 1; while(nn < sz) nn *= 2;
        node.resize(2*nn-1, INF);
    }
    void update(int x, ll val) {
        x += (nn - 1);
        node[x] = val;
        while(x > 0) {
            x = (x - 1) / 2;
            node[x] = min(node[2*x+1], node[2*x+2]);
        }
    }
    ll getmin(int a, int b, int k=0, int l=0, int r=-1) {
        if(r < 0) r = nn;
        if(r <= a || b <= l) return INF;
        if(a <= l && r <= b) return node[k];
        ll vl = getmin(a, b, 2*k+1, l, (l+r)/2);
        ll vr = getmin(a, b, 2*k+2, (l+r)/2, r);
        return min(vl, vr);
    }
    ll rmq(int a,int b){
        return getmin(a,b,0,0,-1);
    }
};
main(){
    ll n=lin(),e=lin(),t=lin();
    ll x[N]={};
    rep2(i,1,n)x[i]=lin();
    ll dp[N]={};
    SegmentTree seg1(n+2),seg2(n+2);
    seg1.update(0,-x[1]*2);
    seg2.update(0,0);
    rep2(i,1,n){
        int l=0,r=i-1;
        while(l<r-1){
            int mid=(l+r)/2;
            if(t-x[i]*2+x[mid+1]*2>0){
                r=mid;
            }
            else l=mid;
        }
        ll m1=seg1.rmq(0,r);
        ll m2=seg2.rmq(r,i)+t-x[i]*2;
        dp[i]=3*x[i]+min(m1,m2);
        seg1.update(i,dp[i]-(x[i]+2*x[i+1]));
        seg2.update(i,dp[i]-x[i]);
    }
    cout<<dp[n]+(e-x[n]);
}

