long long dp[N],dp1[4*N],lazy[4*N],pos[N];
void upd(int idx,int l,int r,int lef,int rig,int val){
	if(l>rig||r<lef){
		return;
	}
	if(l>=lef&&r<=rig){
		if(dp1[idx]<=val){
			return;
		}
		else{
			if(dp1[idx]>val){
				lazy[idx]=val;
				dp1[idx]=val;
			}
			return;
		}
	}
	if(lazy[idx]!=inf){
		dp1[2*idx]=min(lazy[idx],dp1[2*idx]);
		dp1[2*idx+1]=min(dp1[2*idx+1],lazy[idx]);
		lazy[2*idx]=min(lazy[2*idx],lazy[idx]);
		lazy[2*idx+1]=min(lazy[2*idx+1],lazy[idx]);
		lazy[idx]=inf;
	}
	upd(2*idx,l,(l+r)/2,lef,rig,val);
	upd(2*idx+1,(l+r)/2+1,r,lef,rig,val);
	dp1[idx]=max(dp1[2*idx],dp1[2*idx+1]);
}
long long getans(int idx,int l,int r,int pos1){
	if(l>pos1||r<pos1){
		return inf;
	}
	if(l==r){
		return dp1[idx];
	}
	if(lazy[idx]!=inf){
		dp1[2*idx]=min(lazy[idx],dp1[2*idx]);
		dp1[2*idx+1]=min(dp1[2*idx+1],lazy[idx]);
		lazy[2*idx]=min(lazy[2*idx],lazy[idx]);
		lazy[2*idx+1]=min(lazy[2*idx+1],lazy[idx]);
		lazy[idx]=inf;
	}
	return min(getans(2*idx,l,(l+r)/2,pos1),getans(2*idx+1,(l+r)/2+1,r,pos1));
}
