#define MAX_N (1<<17) // 10^5

struct SegTree {
  long long seg[MAX_N*2-1];
  SegTree() {
    for (int i=MAX_N*2-2; i>=0; i--) seg[i] = INF;
  }

  void update(int i, long long x) {
    i += MAX_N-1;
    if (seg[i] <= x) return;
    seg[i] = x;
    while (i > 0) {
      i = (i-1) / 2;
      seg[i] = min(seg[i*2+1], seg[i*2+2]);
    }
  }
  long long query(int a, int b, int k=0, int l=0, int r=MAX_N) {
    if (r <= a || b <= l) return INF;
    if (a <= l && r <= b) return seg[k];
    return min(
      query(a, b, k*2+1, l, (l+r)/2),
      query(a, b, k*2+2, (l+r)/2, r)
    );
  }
};

