#include<bits/stdc++.h>
using namespace std;

typedef long long ll;

const int MN = 100002;

int N, E, T;
vector<ll> X;
vector<ll> dp;

struct BIT {
    vector<ll> tree;
    void init() {
        tree = vector<ll>(4*MN, 1e18);
    }
    void upd(int idx, ll val, int l, int r, int n) {
        if(idx < l || r < idx) return;
        if(l == r) {
            tree[n] = val;
            return;
        }
        int m = (l + r)>>1;
        upd(idx, val, l, m, 2*n);
        upd(idx, val, m + 1, r, 2*n + 1);
        tree[n] = min(tree[2*n], tree[2*n + 1]);
    }
    ll quer(int a, int b, int l, int r, int n) {
        if(b < l || r < a) return 1e18;
        if(a <= l && r <= b) return tree[n];
        int m = (l + r)>>1;
        ll L = quer(a, b, l, m, 2*n);
        ll R = quer(a, b, m + 1, r, 2*n + 1);
        return min(L, R);
    }
} bit1, bit2;

int main() {
    scanf("%d %d %d", &N, &E, &T);

    X.push_back(0);
    for(int i = 0; i < N; i++) {
        int t; scanf("%d", &t);
        X.push_back(t);
    }

    dp.resize(X.size());
    dp[X.size() - 1] = E - X.back();

    bit1.init();
    bit2.init();
    bit1.upd(X.size() - 1, X.back() + dp[X.size() - 1], 0, MN - 1, 1);
    bit2.upd(X.size() - 1, 3*X.back() + dp[X.size() - 1], 0, MN - 1, 1);

    for(int i = (int)X.size() - 2; i >= 0; i--) {
        int s = i + 1, e = (int)X.size() - 1;
        int x = i;
        while(s <= e) {
            int m = (s + e)>>1;

            if(X[m] - X[i + 1] <= T/2) {
                x = m;
                s = m + 1;
            }
            else e = m - 1;
        }

        dp[i] = min(-X[i] + bit1.quer(i + 1, x, 0, MN - 1, 1) + T, -X[i] - 2*X[i + 1] + bit2.quer(x + 1, MN - 1, 0, MN - 1, 1));
        bit1.upd(i, X[i] + dp[i], 0, MN - 1, 1);
        bit2.upd(i, 3*X[i] + dp[i], 0, MN - 1, 1);
    }

    cout << dp[0];
}

