    update(0,n-1,0,t,0);
    update(0,n-1,0,0,t,0);

    rep(i,1,n-1){
        int st=0,en=i-1;
        while(st<=en){
            int mid=getMid(st,en);
            if(pos[i]-pos[mid+1]>t){
                st=mid+1;
            }else{
                en=mid-1;
            }
        }
     //   cout<<st<<endl;
        memo[i]=getmin(0,n-1,0,st-1,0);

     //   cout<<i<<" "<<memo[i]<<endl;

        // to decide if it's better to return to the begining
        memo[i]=min(memo[i],(ll)max(t,2*(pos[i]-pos[0])));
       // cout<<i<<" "<<memo[i]<<endl;
        memo[i]=min(memo[i],getmin2(0,n-1,st,i-1,0)+t);
        memo[i]=min(memo[i],t+memo[i-1]);
       // cout<<i<<" "<<memo[i]<<endl;

        update(0,n-1,0,i-1,2*(pos[i+1]-pos[i]),0);
        update(0,n-1,i,i,memo[i],0);
        update(0,n-1,i,memo[i],0);

    }

