	for(int i = 1; i < N; i++)
	{
		while(opt + 1 <= i)
		{
			if(cost(i , opt + 1) < cost(i , opt))
			{
				opt++;
			}
			else
				break;
		}
		dp[i] = cost(i , opt);
	}
	cout << dp[N - 1] + E;
}

