        pll m1=que(a,b,2*k+1,l,(l+r)/2);
        pll m2=que(a,b,2*k+2,(l+r)/2,r);
        return min(m1,m2);
    }
};

int main(){
    ios::sync_with_stdio(false);cin.tie(0);cout.tie(0);
    ll n,e,t;cin>>n>>e>>t;
    vector<ll> x(n+1);
    rep(i,1,n+1)cin>>x[i];
    x[0]=0;
    vector<ll> v;
    rep(i,0,n+1){
        v.push_back(-x[i]+t);
    }
    vector<ll> vv;
    rep(i,0,n){
        vv.push_back(-2*x[i+1]-x[i]);
    }
    ST seg1(v),seg2(vv);
    ll dp[n+1];
    dp[0]=0;
    rep(i,1,n+1){
        seg1.add(i-1,dp[i-1]);
        seg2.add(i-1,dp[i-1]);
        ll ge=x[i]-(t+1)/2;
        ll y=upper_bound(x.begin(),x.end(),ge)-x.begin();
        if(y>0)y--;
        ll ans=inf;
        pll p=seg2.que(0,y);
        Min(ans,3*x[i]+p.first);
        p=seg1.que(y,i);
        Min(ans,x[i]+p.first);
        dp[i]=ans;
    }
