template <typename T>
struct SegmentTree{
  using F = function<T(T,T)>;
  int n;  //ã‚»ã‚°æœ¨ã®å¹…
  F f;  //ãƒ¢ãƒŽã‚¤ãƒ‰ã®ä½œç”¨
  T ti;  //åˆæœŸå€¤
  vector<T> dat;  //ã‚»ã‚°æœ¨ã§ä½¿ã†é…åˆ— ä¸€ç•ªä¸Šã«ä½¿ã‚ã‚Œã‚‹ã®ãŒdat[1] ä¸€ç•ªä¸‹ã®æ®µã®å·¦ç«¯ãŒdat[n]
  SegmentTree(){};
  SegmentTree(F f,T ti):f(f),ti(ti){}  //fãŒãƒ¢ãƒŽã‚¤ãƒ‰ã¨ã—ã¦ã®ä½œç”¨ã€tiãŒå˜ä½å…ƒ
  SegmentTree(F f,T ti,int n_):f(f),ti(ti){
    n=1;
    while(n<n_) n<<=1;
    dat.assign(n<<1,ti);
  }
  SegmentTree(F f,T ti,const vector<T> &v):f(f),ti(ti){
    int n_=v.size();
    n=1;
    while(n<n_) n<<=1;
    dat.assign(n<<1,ti);
    for(int i=0;i<n_;i++)dat[n+i]=v[i];
    for(int i=n-1;i;i--)
      dat[i]=f(dat[(i<<1)|0],dat[(i<<1)|1]);
  }

  T get(int x){
    return dat[x+n];
  }

  void init(int n_){  //ãŸã ã®åˆæœŸåŒ–
    n=1;
    while(n<n_) n<<=1;  //nãŒå®Ÿéš›ã«ã¨ã‚‹ï¼’ã¹ãã®å¹…
    dat.assign(n<<1,ti);  //daté…åˆ—ã¯å¹…ã®ï¼’å€ã‚ã£ã¦ãã®å…¨ã¦ã«tiã‚’ä»£å…¥
  }

  void build(const vector<T> &v){  //vector vã«åˆã‚ã›ãŸåˆæœŸåŒ–
    int n_=v.size();
    init(n_);
    for(int i=0;i<n_;i++) dat[n+i]=v[i];  //v[i]ã‚’ä¸€ç•ªå­ã®æ‰€ã«ä»£å…¥
    for(int i=n-1;i;i--)
      dat[i]=f(dat[(i<<1)|0],dat[(i<<1)|1]);  //iã®å­ä¾›ã¯2*iã€2*i+1
  }

  void set_val(int k,T x){  //ï¼ˆ0-indexedã§ï¼‰kã«xã‚’ä»£å…¥
    dat[k+=n]=x;
    while(k>>=1)  //kãŒã©ã‚“ã©ã‚“è‡ªåˆ†ã®è¦ªã«ãªã£ã¦ã„ãã€0ã«ãªã£ãŸã‚‰ãŠã—ã¾ã„
      dat[k]=f(dat[(k<<1)|0],dat[(k<<1)|1]);
  }

  T query(int a,int b){  //[a,b)ã®ç¯„å›²ã®ãƒ¢ãƒŽã‚¤ãƒ‰ã®ä½œç”¨ã‚’è¿”ã™
    T vl=ti,vr=ti;
    for(int l=a+n,r=b+n;l<r;l>>=1,r>>=1) {
      if(l&1) vl=f(vl,dat[l++]);  //å·¦ç«¯ã‚’ä½¿ã„ãŸããªã‚‹ã®ã¯ãƒ–ãƒ­ãƒƒã‚¯ãŒå¥‡æ•°ã®æ™‚
      if(r&1) vr=f(dat[--r],vr);  //å³ç«¯ã€ƒå¶æ•°ã®æ™‚ï¼ˆé–‹åŒºé–“ãªã®ã§r:å¥‡æ•°ã§åå¿œï¼‰
    }
    return f(vl,vr);
  }

  template<typename C>
  int find(int st,C &check,T &acc,int k,int l,int r){  //dat[k]:[l,r)ã®é–“ã«ã„ã‚‹enã‚’è¿”ã™
    //ä¸‹ã®findç”¨ãªã®ã§å…ˆã«ãã£ã¡èª­ã‚€ã¹ã
    //accã¯st~lã¨ã‹ã§ç¢ºå®šã—ã¦ã„ã‚‹å€¤
    if(l+1==r){  //ç­”ãˆãŒã¡ã‚ƒã‚“ã¨å­˜åœ¨ã™ã‚‹ãªã‚‰ã“ã®lãŒç­”ãˆ
      acc=f(acc,dat[k]);
      return check(acc)?k-n:-1; //å…¥ã‚‹ãªã‚‰k-n(==l)ã‚’ã€å…¥ã‚‰ãªã„ãªã‚‰-1ã‚’è¿”ã™
    }
    int m=(l+r)>>1;  //ã«ã¶ãŸã‚“ã®ãã‚Œ
    if(m<=st) return find(st,check,acc,(k<<1)|1,m,r);  //mãŒstã‚ˆã‚Šå¾Œã‚ãªã‚‰æ˜Žã‚‰ã‹ã«l=m;
    if(st<=l&&!check(f(acc,dat[k]))){  //ä»Šè¦‹ã¦ã‚‹kå…¨éƒ¨ã§ã‚‚ãƒ€ãƒ¡(mã ã¨è¶³ã‚Šãªã„ã®ã§l=mã«ã™ã‚‹ï¼‰
      acc=f(acc,dat[k]);  //kã¯å…¨éƒ¨accã«åŠ ãˆãŸä¸Šã§-1ã‚’è¿”ã™
      return -1;
    }
    int vl=find(st,check,acc,(k<<1)|0,l,m);  //ã¾ãš[l,m)ã®ä¸­ã«enãŒã„ã‚‹ã‹æŽ¢ã™
    if(~vl) return vl;  //ã„ãŸãªã‚‰ãã‚Œã‚’è¿”ã™(if(~vl)ã¯-1ä»¥å¤–ã«ã®ã¿åå¿œã™ã‚‹)
    return find(st,check,acc,(k<<1)|1,m,r);  //ã„ãªã‹ã£ãŸã‚‰[m,r)ã®ä¸­ã«enãŒã„ã‚‹ã®ã§ãã‚Œã‚’è¿”ã™
    //acc=f(acc,dat[k]);ãŒã“ã“ã§åŠ¹ã„ã¦ã‚‹
  }

  template<typename C>
  int find(int st,C &check){
    //check(f(dat[st]~dat[en-1]))=false,check(f(dat[st]~dat[en]))ï¼trueã¨ãªã‚‹enã‚’è¿”ã™
    T acc=ti;
