template<class T, class T2> inline void chkmax(T &x, const T2 &y) { if(x < y) x = y; }
template<class T, class T2> inline void chkmin(T &x, const T2 &y) { if(x > y) x = y; }
const int MAXN = (1 << 20);

template<class T>
struct monotonous_queue_min
{
	int64_t len;
	deque<pair<T, int64_t> > Q;

	monotonous_queue_min() { Q.clear(); len = 0; }

	void init(int64_t _l)
	{
		len = _l;
		Q.clear();
	}

	void push_back(T x, int64_t X)
	{
		while(!Q.empty() && X - Q.front().second > len) Q.pop_front();
		while(!Q.empty() && x <= Q.back().first) Q.pop_back();
		Q.push_back({x, X});
	}

	T query()
	{
		if(Q.empty()) return (T)1e18;
		return Q.front().first;
	}
};

int64_t n, t, e, L;
int64_t x[MAXN];

void read()
{
	cin >> n >> e >> t;
	for(int i = 1; i <= n; i++)
		cin >> x[i];
}

int64_t dp[MAXN];

/*

   dp[i] = MIN ( 2 * x[i] - 2 * x[j] + dp[j - 1] - x[j - 1] + x[i] + MAX(t - 2 * x[i] + 2 * x[j], 0) )
   dp[i] = 3 * x[i] + MIN(dp[j - 1] - x[j] * 2 - x[j - 1])  
   dp[i] = 3 * x[i] + MIN(dp[j - 1] - x[j] * 2 - x[j - 1] + t - 2 * x[i] + 2 * x[j])

   dp[i] = dp[j - 1] + 3 * x[i] - 2 * x[j] - x[j - 1] 

   t >= x[i] * 2 - x[j] * 2
   floor(t / 2) >= x[i] - x[j]
*/

int64_t mn[MAXN];

monotonous_queue_min<int64_t> mq;

void solve()
{
	x[0] = 0;
	dp[0] = 0;
	mn[0] = (int64_t)1e18;
	L = t / 2;

	mq.init(L);	

	int last = 1;
	for(int i = 1; i <= n; i++)
	{
		/*for(int j = i; j >= 1; j--)
		{
			int64_t wait_t = max((int64_t)0, t - 2 * (x[i] - x[j]));
			chkmin(dp[i], -x[j - 1] + wait_t + (-x[j]) * (int64_t)2 + dp[j - 1]);
		}*/

		mn[i] = min(mn[i - 1], dp[i - 1] - x[i] * (int64_t)2 - x[i - 1]);
		mq.push_back(dp[i - 1] - x[i - 1], x[i]);
		dp[i] = mq.query() + t - 2 * x[i];
		while(i >= last && (x[i] - x[last]) > L) last++;
		chkmin(dp[i], mn[last - 1]);
		dp[i] += 3ll * x[i];
	}

	cout << dp[n] + e - x[n] << endl;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(NULL);

	read();
	solve();
	return 0;
}

