template<class T> class SegmentTree{
public:
	const int size,n2;vector<T> dat;

	T e(){ return INF<T>();}
	T c(T a,T b){return min(a,b);}

	// T me(){return 0;}
	// T mc(T a,T b){return a + b;}
	SegmentTree(int n):size(n),n2(upper_pow2(size)){
		dat=vector<T>(n2*2,e());
	}
	inline T get(int v){return dat[v+n2];}
	void set(int v,T a){
		v+=n2;dat[v]=a;
		while(v!=1){
			int parent=v/=2,chl=parent*2,chr=parent*2+1;
			dat[parent]=c(dat[chl],dat[chr]);
		}
	}
	T query(int a,int b){return query(a,b,1,0,n2);} //[a,b)
private:
	T query(int a,int b,int v,int l,int r){ //[a,b) on v:[l,r)
		if(r<=a || b<=l)return e();//out of range
		if(a<=l && r<=b)return dat[v];
		T lv=query(a,b,v*2,l,(l+r)/2),rv=query(a,b,v*2+1,(l+r)/2,r);
		return c(lv,rv);
	}
};

template <typename T,typename Func>
T satisfy_mini(Func P,T l ,T r){//(l |sep| r)
	l--;
	while(r-l>EPS<T>()){
		T m=(l+r)/2;
		(P(m)?r:l)=m;
	}
	return r;
}


