// [Left, Right]
int64 getCost(int Left, int Right)
{
  if(Left > Right) {
    return (0);
  } else if(Left == Right) {
    return (T);
  } else {
    // Right ã«ã„ã¦ Left ã«æˆ»ã‚‹
    int64 Time = X[Right] - X[Left];
    int64 ret = Time * 2;
    // E çµŒéŽã—ã¦ãªã‹ã£ãŸã‚‰ãã®åˆ†ã‚’åŠ ãˆã‚‹
    ret += max(0LL, T - Time - 2);
    return (ret);
  }
}

// endd å›žåŽæ¸ˆã¿(é–‰åŒºé–“)
int64 rec(int endd, int idx)
{
  if(idx > N) return (INF);
  if(endd == N && idx == N) return (0);
  if(idx == N || endd == N) return (INF);
  if(~dp[endd][idx]) return (dp[endd][idx]);
  int64 ret = INF;
  ret = min(ret, rec(idx + 1, idx + 1) + getCost(endd, idx) + X[idx + 1] - X[idx]);
  ret = min(ret, rec(endd, idx + 1) + X[idx + 1] - X[idx]);
  return (dp[endd][idx] = ret);
}

