const ll N=1e5+5,M=1e1+5,MOD=1e9+7,INF=1e18;

int pos[N],n,t,e;
ll memo[N];
ll seg[4*N],lazy[4*N];

int getMid(int st,int en){
    return (st+en)/2;
}
ll build(int st,int en,int frst){
    if(st==en){
        return seg[frst]=0;
    }
    int mid=getMid(st,en);
    return seg[frst]=min(build(st,mid,frst*2+1),build(mid+1,en,frst*2+2));
}
ll getmin(int st,int en,int qs,int qe,int frst){
    if(qs>qe)return INF;
    if(lazy[frst]){
        seg[frst]+=lazy[frst];
        if(st!=en) {
            lazy[frst * 2 + 1] += lazy[frst];
            lazy[frst * 2 + 2] += lazy[frst];
        }
        lazy[frst]=0;
    }
    if(st>qe || qs>en)return INF;
    if(st>=qs && en<=qe){
        return seg[frst];
    }
    int mid=getMid(st,en);
    return min(getmin(st,mid,qs,qe,frst*2+1),getmin(mid+1,en,qs,qe,frst*2+2));
}
void update(int st,int en,int qs,int qe,ll val,int frst){
    if(lazy[frst]){
        seg[frst]+=lazy[frst];
        if(st!=en) {
            lazy[frst * 2 + 1] += lazy[frst];
            lazy[frst * 2 + 2] += lazy[frst];
        }
        lazy[frst]=0;
    }
    if(st>qe || en<qs)return;
    if(st<=qs && en>=qe){
        lazy[frst]+=val;
        return ;
    }
    int mid=getMid(st,en);
    update(st,mid,qs,qe,val,frst*2+1);
    update(mid+1,en,qs,qe,val,frst*2+2);
    seg[frst]=min(seg[frst*2+1],seg[frst*2+2]);
}

ll seg2[4*N];

ll build2(int st,int en,int frst){
    if(st==en){
        return seg2[frst]=0;
    }
    int mid=getMid(st,en);
    return seg2[frst]=min(build2(st,mid,frst*2+1),build2(mid+1,en,frst*2+2));
}
ll getmin2(int st,int en,int qs,int qe,int frst){
    if(qs>qe)return INF;
    if(st>qe || qs>en)return INF;
    if(st>=qs && en<=qe){
        return seg2[frst];
    }
    int mid=getMid(st,en);
    return min(getmin2(st,mid,qs,qe,frst*2+1),getmin2(mid+1,en,qs,qe,frst*2+2));
}
ll update(int st,int en,int indexToUpdate,ll val,int frst){
    if(st>indexToUpdate || en<indexToUpdate)return seg2[frst];
    if(st==en){
        return seg2[frst]=val;
    }
    int mid=getMid(st,en);
    return seg2[frst]=min(update(st,mid,indexToUpdate,val,frst*2+1),
                          update(mid+1,en,indexToUpdate,val,frst*2+2));
}
